CREATE TABLE medicijn (id BIGINT AUTO_INCREMENT, hoofdklasse VARCHAR(255) NOT NULL, generische_naam VARCHAR(255) NOT NULL UNIQUE, specialiteit VARCHAR(255) NOT NULL, magistraat_type VARCHAR(255) NOT NULL, type VARCHAR(255) NOT NULL, bnf_percentage BIGINT NOT NULL, bnf_getal BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE patient (id BIGINT AUTO_INCREMENT, naam VARCHAR(255) NOT NULL, voornaam VARCHAR(255) NOT NULL, geslacht TINYINT(1) NOT NULL, geboortedatum VARCHAR(255), medicatiescheme VARCHAR(255), opmerkingen TEXT, voorgeschiedenis TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE patient_medicijn (id BIGINT AUTO_INCREMENT, patient_id BIGINT NOT NULL, medicijn_id BIGINT NOT NULL, aantal BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
